package me.bunny;

import me.bunny.encoder.Base26Encoder;

public class Main {

    public static void main(String[] args) {
        BunnyHash bunnyHash = new BunnyHash("",
                new Base26Encoder(5));

        for(int i = 0; i < 200; i++) {
            String hash = bunnyHash.hash(String.valueOf(i));
            int rev = bunnyHash.reverse(hash).intValue();
            System.out.println(i + ": " + hash + ", rev: " + rev);
        }

    }

}
